import React, { useState } from "react";

function EditItemModal({ item, onClose, onSave }) {
  const [updatedItem, setUpdatedItem] = useState(item);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (updatedItem.name && updatedItem.category && updatedItem.quantity) {
      onSave(updatedItem.id, {
        name: updatedItem.name,
        category: updatedItem.category,
        quantity: parseInt(updatedItem.quantity, 10),
      });
      onClose();
    } else {
      alert("Please fill out all fields.");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
      <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
        <h2 className="text-3xl font-semibold text-blue-600 mb-6 text-center">
          Edit Item
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block mb-2 text-black font-medium">Name</label>
            <input
              type="text"
              className="border rounded-lg p-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-600"
              value={updatedItem.name}
              onChange={(e) =>
                setUpdatedItem({ ...updatedItem, name: e.target.value })
              }
            />
          </div>
          <div>
            <label className="block mb-1 text-black font-medium">
              Category
            </label>
            <input
              type="text"
              className="border rounded-lg p-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-600"
              value={updatedItem.category}
              onChange={(e) =>
                setUpdatedItem({ ...updatedItem, category: e.target.value })
              }
            />
          </div>
          <div>
            <label className="block mb-1 text-black font-medium">
              Quantity
            </label>
            <input
              type="number"
              className="border rounded-lg p-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-600"
              value={updatedItem.quantity}
              onChange={(e) =>
                setUpdatedItem({ ...updatedItem, quantity: e.target.value })
              }
            />
          </div>
          <div className="flex justify-end space-x-4">
            <button
              type="button"
              className="px-4 py-2 bg-red-500 text-white rounded-lg shadow hover:bg-red-800 transition-all"
              onClick={onClose}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-green-600 text-white rounded-lg shadow hover:bg-blue-600 transition-all"
            >
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EditItemModal;
