import React, { useState, useEffect } from "react";
import { collection, getDocs, addDoc, updateDoc, deleteDoc, doc } from "firebase/firestore";
import { db } from "../firebase/firebaseConfig"; // Import Firebase configuration
import AddItemModal from "./AddItemModal";
import EditItemModal from "./EditItemModal";
import DeleteItemModal from "./DeleteItemModal";
import '../styles.css';

function InventoryTable() {
  const [items, setItems] = useState([]);
  const [addModalOpen, setAddModalOpen] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [deleteItem, setDeleteItem] = useState(null);
  const [filterCategory, setFilterCategory] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [searchQuery, setSearchQuery] = useState('');
  const [filterItems, setFilterItems] = useState(items);

  const fetchItems = async () => {
    const snapshot = await getDocs(collection(db, "inventory"));
    const itemList = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
    setItems(itemList);
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const addItem = async (item) => {
    await addDoc(collection(db, "inventory"), item);
    fetchItems();
  };

  const updateItem = async (id, updatedItem) => {
    await updateDoc(doc(db, "inventory", id), updatedItem);
    fetchItems();
  };

  const deleteItemWithConfirmation = async (id) => {
    await deleteDoc(doc(db, "inventory", id));
    fetchItems();
    setDeleteItem(null);
  };

  const filteredItems = items.filter((item) =>
    filterCategory ? item.category.toLowerCase().includes(filterCategory.toLowerCase()) : true
  );

  const sortedItems = [...filteredItems].sort((a, b) =>
    sortOrder === "asc" ? a.quantity - b.quantity : b.quantity - a.quantity
  );

  // Handle search query change
  const handleSearch = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(query);
    // Filter items based on the query
    const filter = items.filter(item => 
      item.name.toLowerCase().includes(query) ||
      item.category.toLowerCase().includes(query)
    );
    setFilterItems(filter);
  };

  // Clear search input
  const clearSearch = () => {
    setSearchQuery('');
    setFilterItems(items); // Reset filtered items
  };

  return (
    <div className="bg-gradient-to-r from-blue-200 to-purple-300 min-h-screen p-6">
      <div className="max-w-7xl mx-auto bg-white shadow-xl rounded-lg p-6">
        <h1 className="text-6xl font-serif font-bold text-transparent bg-clip-text bg-gradient-to-r from-teal-400 via-blue-500 to-purple-600 mb-8 text-center leading-tight">
          Inventory Management
        </h1>
        <div className="flex justify-between items-center mb-6">
          <input
            type="text"
            placeholder="Filter by category"
            className="border border-gray-300 rounded-md p-3 w-1/3 shadow-md focus:outline-none focus:ring-2 focus:ring-teal-200"
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
          />
          <input
            type="text"
            placeholder="Search items"
            value={searchQuery}
            onChange={handleSearch}
            className="border rounded-l-md p-2 w-80"
          />
        <button
          className="bg-blue-500 text-white p-2 rounded-r-md hover:bg-blue-600"
          onClick={clearSearch}
        >
          Clear
        </button>
          <button
            className="bg-blue-400 text-white px-6 py-3 rounded-lg shadow-lg hover:bg-blue-800 transition-all ease-in-out duration-300"
            onClick={() => setAddModalOpen(true)}
          >
            + Add Item
          </button>
        </div>
        <div className="overflow-x-auto shadow-lg rounded-lg">
          <table className="min-w-full bg-white rounded-lg shadow-md border-collapse" r>
            <thead>
              <tr className="bg-gray-100 text-gray-600 uppercase text-sm leading-normal">
                <th className="py-4 px-6 text-left">Name</th>
                <th className="py-4 px-6 text-left">Category</th>
                <th className="py-4 px-6 text-left flex items-center justify-center">Quantity
                  <button
                    className="flex items-center text-gray-600 hover:text-blue-300 focus:outline-none transition-all duration-300 ease-in-out ml-1"
                    onClick={() => setSortOrder(sortOrder === "asc" ? "desc" : "asc")}
                  >
                  <span className="text-sm" >{sortOrder === "asc" ? "↑" : "↓"}</span>
                  </button>
                </th>
                <th className="py-4 px-6 text-center">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-600 text-sm font-light">
              {sortedItems.map((item) => (
                <tr
                  key={item.id}
                  
                >
                  <td className="py-3 px-6 text-left">{item.name}</td>
                  <td className="py-3 px-6 text-left">{item.category}</td>
                  <td className="py-3 px-6 text-center">{item.quantity}</td>
                  <td className="py-3 px-6 text-center">
                    <button
                      className="bg-yellow-400 text-white px-4 py-2 rounded-lg shadow-md hover:bg-yellow-600 transition-all mr-2"
                      onClick={() => setEditItem(item)}
                    >
                      Edit
                    </button>
                    <button
                      className="bg-red-400 text-white px-4 py-2 rounded-lg shadow-md hover:bg-red-700 transition-all"
                      onClick={() => setDeleteItem(item)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {addModalOpen && (
        <AddItemModal onClose={() => setAddModalOpen(false)} onAdd={addItem} />
      )}
      {editItem && (
        <EditItemModal
          item={editItem}
          onClose={() => setEditItem(null)}
          onSave={updateItem}
        />
      )}
      {deleteItem && (
        <DeleteItemModal
          itemName={deleteItem.name}
          onClose={() => setDeleteItem(null)}
          onDelete={() => deleteItemWithConfirmation(deleteItem.id)}
        />
      )}
    </div>
  );
}

export default InventoryTable;
