import React, { useState } from "react";

function AddItemModal({ onClose, onAdd, nightMode }) {
  const [item, setItem] = useState({ name: "", category: "", quantity: "" });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (item.name && item.category && item.quantity) {
      onAdd({ ...item, quantity: parseInt(item.quantity, 10) });
      onClose();
    } else {
      alert("Please fill out all fields.");
    }
  };

  return (
    <div className={`fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center transition-all ease-in-out duration-300 ${nightMode ? "bg-opacity-80" : "bg-opacity-50"}`}>
      <div className={`p-6 rounded-lg shadow-lg w-96 transition-all ease-in-out duration-300 ${nightMode ? "bg-gray-800 text-white" : "bg-gray-200 text-black"}`}>
        <h2 className="text-3xl font-semibold text-center mb-4">
          Add New Item
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className={`block text-sm font-medium mb-2 ${nightMode ? "text-gray-300" : "text-black"}`}>
              Name
            </label>
            <input
              type="text"
              className={`w-full border rounded-lg p-3 focus:ring-2 focus:ring-blue-600 focus:outline-none ${nightMode ? "bg-gray-700 text-white border-gray-600" : "bg-white text-black border-gray-300"}`}
              value={item.name}
              onChange={(e) => setItem({ ...item, name: e.target.value })}
            />
          </div>
          <div className="mb-4">
            <label className={`block text-sm font-medium mb-2 ${nightMode ? "text-gray-300" : "text-black"}`}>
              Category
            </label>
            <input
              type="text"
              className={`w-full border rounded-lg p-3 focus:ring-2 focus:ring-blue-600 focus:outline-none ${nightMode ? "bg-gray-700 text-white border-gray-600" : "bg-white text-black border-gray-300"}`}
              value={item.category}
              onChange={(e) => setItem({ ...item, category: e.target.value })}
            />
          </div>
          <div className="mb-4">
            <label className={`block text-sm font-medium mb-2 ${nightMode ? "text-gray-300" : "text-black"}`}>
              Quantity
            </label>
            <input
              type="number"
              className={`w-full border rounded-lg p-3 focus:ring-2 focus:ring-blue-600 focus:outline-none ${nightMode ? "bg-gray-700 text-white border-gray-600" : "bg-white text-black border-gray-300"}`}
              value={item.quantity}
              onChange={(e) => setItem({ ...item, quantity: e.target.value })}
            />
          </div>
          <div className="flex justify-end space-x-4">
            <button
              type="button"
              className={`px-4 py-2 rounded-lg shadow transition-all ease-in-out duration-300 ${nightMode ? "bg-red-600 text-white hover:bg-red-800" : "bg-red-500 text-white hover:bg-red-800"}`}
              onClick={onClose}
            >
              Cancel
            </button>
            <button
              type="submit"
              className={`px-4 py-2 rounded-lg shadow transition-all ease-in-out duration-300 ${nightMode ? "bg-green-600 text-white hover:bg-green-800" : "bg-green-500 text-white hover:bg-green-800"}`}
            >
              Add
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AddItemModal;
